    /// </summary>
    public string ExistingSeasons { get; set; } = NoSeasons;
    private const string NoSeasons = "DefaultSeason|0|0";

    // ReSharper disable once InconsistentNaming
    public record _Season(string Label, DateTime StartDate, int MatchCount);
    [NotMapped]
    public _Season[] Seasons
    {
        get
        {
            _Season[] seasons = [.. ExistingSeasons.Split(';').Select(s =>
            {
                string[] values = s.Split('|');
                DateTime.TryParse(values[1], out DateTime date);
                int.TryParse(values[2], out int matchCount);
                return new _Season(values[0], date, matchCount);
            }).OrderBy(s => s.StartDate)];

            return seasons.Length > 0 ? seasons : [new("DefaultSeason", DateTime.UtcNow, 0)];
        }
    }

    public void IncrementSeasonMatchCount()
    {
        List<_Season> seasons = [.. Seasons];
        _Season current = seasons.Single(s => s.Label == DomainConfig.Season);
        seasons.Remove(current);
        _Season updated = new(current.Label, current.StartDate, current.MatchCount + 1);
        seasons.Add(updated);

        ExistingSeasons = NoSeasons;

        foreach (_Season? season in seasons)
            AddSeason(season);
    }

    public void AddSeason(_Season season) => AddSeason(season.Label, season.StartDate, season.MatchCount);
    public void AddSeason(string seasonName, DateTime startDate, int matchCount)
    {
        if (Seasons.Any(s => s.Label.Equals(seasonName, StringComparison.CurrentCultureIgnoreCase))) return;
        string newSeason = $"{seasonName}|{startDate}|{matchCount}";
        if (Seasons[^1].MatchCount == 0)
        {
            //if only one season exists(usually the default), it will be replaced with a new season
            //Note that if it is not used, it will be replaced upon creating a new season
            if (Seasons.Length is 1)
            {
                ExistingSeasons = newSeason;
            }
            else
            {
                //If there are multiple seasons, it will remove all seasons without matches played before adding
                RemoveLastSeason();
                AddSeason(seasonName, startDate, matchCount);
            }

            return;
        }

        ExistingSeasons += $";{newSeason}";
    }

    public void RemoveLastSeason() => ExistingSeasons = string.Join(";", ExistingSeasons.Split(";")[..^1]).Trim(';');
}
